import React, { useState, useEffect } from 'react';
import './App.css';
import Result from './Components/result';


const algoliasearch = require('algoliasearch');
const client = algoliasearch('YR9EHTUZLF', '0fa905cc79c37865c895cbd920b33420');
const index = client.initIndex('test_firstSearchIndex');
var Hit;
function search(searchKey) {
  index.search({ 'query': searchKey })
    .then(({ hits } = {}) => {
      console.log(hits);
      Hit = hits;
    }).catch(err => {
      console.log(err);
      console.log(err.debugData);
    });
}

function App() {
  const [searchKey, setSearchKey] = useState("");
  const [empty, SetEmpty] = useState(true);
  useEffect(() => {
    console.log(`Enter Key = ${searchKey}`);
  });

  function enterFunction(event) {
    search(searchKey);
    SetEmpty(false);
  }
  return (
    <div className="App">
      <p>
        Algolia Search
      </p>
      <input type="text" onKeyPress={event => {
        if (event.key === 'Enter') {
          enterFunction(event)
        }else{
          SetEmpty(true);
        }
      }} onChange={(event) => setSearchKey(event.target.value)} />

      { empty && <Result hits={Hit} />}
    </div>
  );
}

export default App;
